// client/src/components/Input.js
import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import { useNavigate } from "react-router-dom";
import API_BASE_URL from "../api";

const Input = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    jobNumber: "",
    customerName: "",
    brand: "",
    phoneNumber: "",
  });
  const [error, setError] = useState(null);

  // handle field updates
  const handleChange = (e) => {
    const { name, value } = e.target;
    // Allow only numbers for phone number
    if (name === "phoneNumber" && !/^\d*$/.test(value)) return;

    setForm((prev) => ({ ...prev, [name]: value }));
    if (error) setError(null); // Clear error when user types
  };

  // handle submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    // Frontend Validation
    if (!form.jobNumber || !form.customerName || !form.brand || !form.phoneNumber) {
      setError("All fields are required.");
      return;
    }

    if (form.phoneNumber.length !== 10) {
      setError("Phone number must be exactly 10 digits.");
      return;
    }

    try {
      const res = await fetch(`${API_BASE_URL}/api/items`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });

      const data = await res.json();

      if (res.ok) {
        console.log("✅ Saved:", data);
        alert("Data saved successfully!");
        setForm({ jobNumber: "", customerName: "", brand: "", phoneNumber: "" });
        navigate("/");
      } else {
        console.error("❌ Error saving:", data);
        setError(data.error || "Failed to save data.");
      }
    } catch (err) {
      console.error("⚠️ Network error:", err);
      setError("Error connecting to server.");
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      style={{
        display: "flex",
        flexDirection: "column",
        gap: "1rem",
        width: "300px",
        margin: "2rem auto",
      }}
    >
      <TextField
        name="jobNumber"
        label="Job Number"
        value={form.jobNumber}
        onChange={handleChange}
        required
        error={!!error}
      />
      <TextField
        name="customerName"
        label="Customer Name"
        value={form.customerName}
        onChange={handleChange}
        required
      />
      <TextField
        name="brand"
        label="Brand"
        value={form.brand}
        onChange={handleChange}
        required
      />
      <TextField
        name="phoneNumber"
        label="Phone Number"
        value={form.phoneNumber}
        onChange={handleChange}
        required
        inputProps={{ maxLength: 10 }} // Hint to limit length
      />

      {error && (
        <Typography color="error" variant="body2" align="center">
          {error}
        </Typography>
      )}

      <div style={{ display: "flex", gap: "1rem", justifyContent: "center" }}>
        <Button type="submit" variant="contained" color="primary">
          Save
        </Button>
        <Button variant="outlined" onClick={() => navigate("/items")}>
          VIEW
        </Button>
      </div>
    </form>
  );
};

export default Input;
